import Avatar from 'react-avatar';
import React, { useEffect, useRef, useState } from 'react';

const Video = ({ socket, roomId, username, isLocalUser }) => {
    const localVideoRef = useRef();
    const remoteVideoRef = useRef();
    const peerConnection = new RTCPeerConnection();
    const [videoEnabled, setVideoEnabled] = useState(true);

    useEffect(() => {
        const initializeMedia = async () => {
            try {
                const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
                localVideoRef.current.srcObject = stream;
                peerConnection.addStream(stream);
            } catch (error) {
                console.error('Error accessing media devices:', error);
            }
        };

        initializeMedia();

        peerConnection.onaddstream = (event) => {
            remoteVideoRef.current.srcObject = event.stream;
        };

        peerConnection.onicecandidate = (event) => {
            if (event.candidate) {
                socket.emit('ice-candidate', { roomId, candidate: event.candidate });
            }
        };

        socket.on('offer', (data) => {
            peerConnection.setRemoteDescription(new RTCSessionDescription(data.offer));
            peerConnection.createAnswer()
                .then((answer) => {
                    peerConnection.setLocalDescription(answer);
                    socket.emit('answer', { roomId, answer });
                })
                .catch((error) => console.error('Error creating answer:', error));
        });

        socket.on('answer', (data) => {
            peerConnection.setRemoteDescription(new RTCSessionDescription(data.answer));
        });

        peerConnection.createOffer()
            .then((offer) => {
                peerConnection.setLocalDescription(offer);
                socket.emit('offer', { roomId, offer });
            })
            .catch((error) => console.error('Error creating offer:', error));

    }, []);

    const toggleVideo = () => {
        setVideoEnabled((prev) => !prev);
    
        // Get the video track
        const videoTrack = localVideoRef.current.srcObject.getVideoTracks()[0];
    
        // Enable or disable the video track based on the new state
        videoTrack.enabled = !videoTrack.enabled;
    };
    
    

    return (
        <div className='videoContainer'>
            <span className="userName">{username}</span>
            <div className='videoFeature'>
                {videoEnabled ? (
                    <video className='videoFeature' ref={localVideoRef} autoPlay playsInline muted />
                    ) : (
                        <Avatar className='videoFeature' name={username} size={100} round="1px" />
                )}
                {isLocalUser && (
                    <button onClick={toggleVideo} style={{ backgroundColor: !videoEnabled ? '#4aed88' : '#E4312b' }}>
                        {videoEnabled ? 'Disable Video' : 'Enable Video'}
                    </button>
                )}
            </div>
            {remoteVideoRef.current && <video className='videoFeature' ref={remoteVideoRef} autoPlay playsInline />}
        </div>
    );
};

export default Video;
