// Client.js

import React, { useState, useEffect } from 'react';
import Avatar from 'react-avatar';

const Client = ({ username, stream }) => {
    const [videoEnabled, setVideoEnabled] = useState(true);

    useEffect(() => {
        // Update the video track enabled state when the videoEnabled state changes
        if (stream) {
            const videoTrack = stream.getVideoTracks()[0];
            if (videoTrack) {
                videoTrack.enabled = videoEnabled;
            }
        }
    }, [videoEnabled, stream]);

    const toggleVideo = () => {
        setVideoEnabled((prev) => !prev);
    };

    return (
        <div className="client">
            {stream ? (
                <>
                    <div className="videoContainer">
                        <video className="clientVideo" srcObject={stream} autoPlay playsInline />
                        <button onClick={toggleVideo}>
                            {videoEnabled ? 'Disable Video' : 'Enable Video'}
                        </button>
                    </div>
                </>
            ) : (
                <Avatar name={username} size={100} round="12px" />
            )}
            <span className="userName">{username}</span>
        </div>
    );
};

export default Client;
